mapper.select:
SELECT DISTINCT s.id AS id, s.NAME as name, s.url AS url, s.description AS description, s.icon AS icon, s.pid AS pid, s.seq AS seq, s.STATUS AS status, s.open_mode AS openMode, s.opened as opened, s.resource_type AS resourceType, s.create_time AS createTime FROM c_role r LEFT JOIN c_role_resource e ON r.id = e.role_id LEFT JOIN c_resource s ON e.resource_id = s.id WHERE r.id in
#{item}
AND s.resource_type = 0 ORDER BY seq

select re.url from c_user u,c_user_role ur,c_role r ,c_role_resource rr,c_resource re where u.id = ur.user_id and ur.role_id = r.id and r.id = rr.role_id and rr.resource_id = re.id and url is not null and pid= #{pid}
and url like CONCAT(CONCAT('',#{url}),'%')
and u.id = #{userId}
