sql.where:
c.renew = #{map.renew}
and TO_CHAR(c.expire_time, 'YYYY-MM') = #{map.expireTime}
and c.internet_card_status =  #{map.internetCardStatus}
and c.status = 1

mapper.select:
select

from o_internet_Card c

  c.renew = #{map.renew}
  and TO_CHAR(c.expire_time, 'YYYY-MM') = #{map.expireTime}
  and Internet_card_status =  #{map.internetCardStatus}
  and c.status = 1


select

from O_INTERNET_CARD o LEFT JOIN d_dict d on d.d_itemvalue = o.MANUFACTURER AND d.d_group ='ORDER' AND d_artifact='MANUFACTURER'




  order by ${orderByClause}
